//SPDX-License-Identifier: MIT
pragma solidity >= 0.5.0 < 0.7.0;
 /*
cuenta que estoy usando
mapping (asignacion) es un tipo de referencia como arrays y estructuras.

Sintaxis de mapeo:  mapping(_keyType => _valueType)
Donde:
_keyType: puede ser cualquier tipo integrado (tipo de construcion)
        mas bytes y string. No se permiten tipos de referencia u objetos complejos.
_valueType: puede ser cualquier tipo.

Consideraciones:
 El mapping (La asignacion) solamente puede tener un tipo de almacenamiento y
generalmente se usa para variables de estado.
 El mapping se puede marcar como public. Solidity crea automaticamente getter para ello.
 */

contract Usuario {
  /*
  constructor() public {
  }
  */
  mapping(address => int256) public saldo;// tiene qu
  /*
   Las funciones modifier (modificadores) se uilizan para modificar el comportamiento de una funcion.
  e.g se usa para agregar un requisito previo a una funcion. Puede o no tener parametros.
   El cuerpo de la funcion se inserta donde aparece el simbolo especial "_;" en la definicion demodifier.
  Entonces, si se cumple la condicion del modifier se llama a esta funcion a traves de "_;", la function
  se ejecuta, de lo contrario se genera una excepcion (o sea no cumple la condicion).
  */
  modifier positivo(int256 valor){
    require(valor > 0, "El valor debe ser positivo");//validacion
    _; //que es? para llamar a la fuancion que le estamos poniendo la condicion
  }
  // cambiar poner por cargar
  function poner(address usuario, int256 cuanto) public positivo (cuanto){
    int256 plata = saldo[usuario]; //Type uint256 is not implicitly convertible to expected type int256.
    plata += cuanto;
    saldo[usuario] = plata;
  }
  //por ahi cambiar sacar por comprar
  function sacar(address usuario, int256 cuanto) public positivo(cuanto){
    int256 dinero = saldo[usuario];
    require(dinero >= cuanto, "No hay suficiente efectivo");
    dinero -= cuanto;
    saldo[usuario] = dinero;
  }
// //tal vez no es necesario
//   function balance(address usuario) public view returns(int256){
//     return saldo[usuario];
//   }
}

